INSERT INTO `email_templates` (`id`, `subject`, `groupId`, `webshopId`, `organizationId`, `type`, `text`, `html`, `json`, `updatedAt`, `createdAt`) VALUES
('f4c7679d-3f46-40e4-b751-aa5ddaf09d87', 'ðŸ›’ {{webshopName}} - Bestelling #{{nr}}', NULL, NULL, NULL, 'OrderNotification', 'Hallo,\nEr werd een nieuwe bestelling geplaatst voor {{webshopName}} ({{organizationName}}).Overzicht\n{{orderDetailsTable}}\n\n{{orderTable}}\nTotaal {{orderPrice}}\n\n\n    \n        \n            \n                \n                Bekijken in Stamhoofd\n                \n            \n        \n    \n\nJe ontvangt deze e-mail omdat dit ingesteld staat bij de instellingen van de webshop (meldingen). Als je deze e-mails niet meer wilt ontvangen, kan je het daar aanpassen. Je kan ook uitschrijven op alle e-mails van {{organizationName}} via deze link: Uitschrijven', '<!DOCTYPE html>\n<html>\n\n<head>\n<meta charset=\"utf-8\" />\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\" />\n<meta name=\"viewport\" content=\"width=device-width,initial-scale=1.0\" />\n<title>ðŸ›’ {{webshopName}} - Bestelling #{{nr}}</title>\n<style type=\"text/css\">body {\n  color: #000716;\n  color: var(--color-dark, #000716);\n  font-family: -apple-system-body, -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, Helvetica, Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\";\n  font-size: 12pt;\n  line-height: 1.4;\n}\n\np {\n  margin: 0;\n  padding: 0;\n  line-height: 1.4;\n}\n\np.description {\n  color: var(--color-gray-4, #5e5e5e);\n}\n\nstrong {\n  font-weight: bold;\n}\n\nem {\n  font-style: italic;\n}\n\nh1 {\n  font-size: 30px;\n  font-weight: bold;\n  line-height: 1.2;\n  margin: 0;\n  padding: 0;\n}\n@media (max-width: 350px) {\n  h1 {\n    font-size: 24px;\n  }\n}\n\nh2 {\n  font-size: 20px;\n  line-height: 1.2;\n  font-weight: bold;\n  margin: 0;\n  padding: 0;\n}\n\nh3 {\n  font-size: 16px;\n  line-height: 1.2;\n  font-weight: bold;\n  margin: 0;\n  padding: 0;\n}\n\nh4 {\n  line-height: 1.2;\n  font-weight: 500;\n  margin: 0;\n  padding: 0;\n}\n\nol, ul {\n  list-style-position: outside;\n  padding-left: 30px;\n}\n\nhr {\n  height: 1px;\n  background: var(--color-border, var(--color-gray-2, #dcdcdc));\n  border-radius: 1px;\n  padding: 0;\n  margin: 20px 0;\n  outline: none;\n  border: 0;\n}\n\n.button {\n  touch-action: inherit;\n  -webkit-user-select: auto;\n     -moz-user-select: auto;\n      -ms-user-select: auto;\n          user-select: auto;\n  cursor: pointer;\n  display: inline-block !important;\n  line-height: 42px;\n  font-size: 16px;\n  font-weight: bold;\n}\n.button:active {\n  transform: none;\n}\n\nimg {\n  max-width: 100%;\n  height: auto;\n}\n\na, a:link, a:visited, a:active, a:hover {\n  text-decoration: underline;\n  color: blue;\n}\n\n.email-data-table {\n  width: 100%;\n  border-collapse: collapse;\n}\n.email-data-table th, .email-data-table td {\n  text-align: left;\n  padding: 10px 10px 10px 0;\n  border-bottom: 1px solid var(--color-border, var(--color-gray-2, #dcdcdc));\n  vertical-align: middle;\n}\n.email-data-table th:last-child, .email-data-table td:last-child {\n  text-align: right;\n  padding-right: 0;\n}\n.email-data-table thead {\n  font-weight: bold;\n}\n.email-data-table thead th {\n  font-size: 10pt;\n}\n.email-data-table h4 + p {\n  padding-top: 3px;\n  opacity: 0.8;\n  font-size: 11pt;\n} hr {height: 2px;background: #e7e7e7; border-radius: 1px; padding: 0; margin: 20px 0; outline: none; border: 0;} .button.primary { margin: 0; text-decoration: none; font-size: 16px; font-weight: bold; color: white; padding: 0 27px; line-height: 42px; background: {{primaryColor}}; text-align: center; border-radius: 7px; touch-action: manipulation; display: inline-block; transition: 0.2s transform, 0.2s opacity; } .button.primary:active { transform: scale(0.95, 0.95); }  .inline-link, .inline-link:link, .inline-link:visited, .inline-link:active, .inline-link:hover { margin: 0; text-decoration: underline; font-size: inherit; font-weight: inherit; color: inherit; touch-action: manipulation; } .inline-link:active { opacity: 0.5; }  .button { color: #5e5e5e; } </style>\n</head>\n\n<body>\n<p style=\"margin: 0px; padding: 0px;\">Hallo,</p><p style=\"margin: 0px; padding: 0px;\"><br></p><p style=\"margin: 0px; padding: 0px;\">Er werd een nieuwe bestelling geplaatst voor {{webshopName}} ({{organizationName}}).</p><hr style=\"height: 2px; background-color: rgb(231, 231, 231); border-radius: 1px; padding: 0px; margin: 20px 0px; outline: none; border: 0px;\"><h2 style=\"margin: 0px; padding: 0px;\">Overzicht</h2><p style=\"margin: 0px; padding: 0px;\"><br></p><div data-type=\"smartVariableBlock\" data-id=\"orderDetailsTable\">{{orderDetailsTable}}</div><p style=\"margin: 0px; padding: 0px;\"><br></p><p style=\"margin: 0px; padding: 0px;\"><br></p><div data-type=\"smartVariableBlock\" data-id=\"orderTable\">{{orderTable}}</div><p style=\"margin: 0px; padding: 0px;\"><br></p><h3 style=\"margin: 0px; padding: 0px;\">Totaal {{orderPrice}}</h3><p style=\"margin: 0px; padding: 0px;\"><br></p><hr style=\"height: 2px; background-color: rgb(231, 231, 231); border-radius: 1px; padding: 0px; margin: 20px 0px; outline: none; border: 0px;\"><div data-type=\"smartButton\" data-id=\"orderUrl\"><table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\n<tbody><tr>\n    <td>\n        <table cellspacing=\"0\" cellpadding=\"0\">\n            <tbody><tr>\n                <td style=\"border-radius: 7px;\" bgcolor=\"{{primaryColor}}\">\n                <a class=\"button primary\" href=\"{{orderUrl}}\" target=\"\" style=\"margin: 0px; text-decoration: none; font-size: 16px; font-weight: bold; color: white; padding: 0px 27px; line-height: 42px; text-align: center; border-radius: 7px; touch-action: manipulation; display: inline-block; transition: transform 0.2s ease 0s, opacity 0.2s ease 0s;\">Bekijken in Stamhoofd</a>\n                </td>\n            </tr>\n        </tbody></table>\n    </td>\n</tr>\n</tbody></table></div><hr style=\"height: 2px; background-color: rgb(231, 231, 231); border-radius: 1px; padding: 0px; margin: 20px 0px; outline: none; border: 0px;\"><p class=\"description\" style=\"color: rgb(94, 94, 94);\"><em>Je ontvangt deze e-mail omdat dit ingesteld staat bij de instellingen van de webshop (meldingen). Als je deze e-mails niet meer wilt ontvangen, kan je het daar aanpassen. Je kan ook uitschrijven op alle e-mails van {{organizationName}} via deze link: </em><a class=\"inline-link\" href=\"{{unsubscribeUrl}}\" target=\"\" style=\"margin: 0px; text-decoration: underline; font-size: inherit; font-weight: inherit; color: inherit; touch-action: manipulation;\"><em>Uitschrijven</em></a></p>\n</body>\n\n</html>', '{\"value\": {\"type\": \"doc\", \"content\": [{\"type\": \"paragraph\", \"content\": [{\"text\": \"Hallo,\", \"type\": \"text\"}]}, {\"type\": \"paragraph\"}, {\"type\": \"paragraph\", \"content\": [{\"text\": \"Er werd een nieuwe bestelling geplaatst voor \", \"type\": \"text\"}, {\"type\": \"smartVariable\", \"attrs\": {\"id\": \"webshopName\"}}, {\"text\": \" (\", \"type\": \"text\"}, {\"type\": \"smartVariable\", \"attrs\": {\"id\": \"organizationName\"}}, {\"text\": \").\", \"type\": \"text\"}]}, {\"type\": \"horizontalRule\"}, {\"type\": \"heading\", \"attrs\": {\"level\": 2}, \"content\": [{\"text\": \"Overzicht\", \"type\": \"text\"}]}, {\"type\": \"paragraph\"}, {\"type\": \"smartVariableBlock\", \"attrs\": {\"id\": \"orderDetailsTable\"}}, {\"type\": \"paragraph\"}, {\"type\": \"paragraph\"}, {\"type\": \"smartVariableBlock\", \"attrs\": {\"id\": \"orderTable\"}}, {\"type\": \"paragraph\"}, {\"type\": \"heading\", \"attrs\": {\"level\": 3}, \"content\": [{\"text\": \"Totaal \", \"type\": \"text\"}, {\"type\": \"smartVariable\", \"attrs\": {\"id\": \"orderPrice\"}}]}, {\"type\": \"paragraph\"}, {\"type\": \"horizontalRule\"}, {\"type\": \"smartButton\", \"attrs\": {\"id\": \"orderUrl\"}, \"content\": [{\"text\": \"Bekijken in Stamhoofd\", \"type\": \"text\"}]}, {\"type\": \"horizontalRule\"}, {\"type\": \"descriptiveText\", \"content\": [{\"text\": \"Je ontvangt deze e-mail omdat dit ingesteld staat bij de instellingen van de webshop (meldingen). Als je deze e-mails niet meer wilt ontvangen, kan je het daar aanpassen. Je kan ook uitschrijven op alle e-mails van \", \"type\": \"text\", \"marks\": [{\"type\": \"italic\"}]}, {\"type\": \"smartVariable\", \"attrs\": {\"id\": \"organizationName\"}, \"marks\": [{\"type\": \"italic\"}]}, {\"text\": \" via deze link: \", \"type\": \"text\", \"marks\": [{\"type\": \"italic\"}]}, {\"type\": \"smartButtonInline\", \"attrs\": {\"id\": \"unsubscribeUrl\"}, \"content\": [{\"text\": \"Uitschrijven\", \"type\": \"text\", \"marks\": [{\"type\": \"italic\"}]}]}]}]}, \"version\": 163}', '2022-08-02 21:17:17', '2022-08-02 21:17:17');